mapper.select:
  		select a.trade_no, (select member_nickname from member_tbl where member_no=a.trade_seller) as trade_seller_nickname ,a.trade_buyer, a.product_no, a.trade_reserve_date, to_char(a.trade_date, 'yyyy/mm/dd hh24:mi') as trade_date, a.trade_price, a.trade_state, a.zipcode,a.address,a.address_detail,a.address_name,a.address_phone,a.address_request,a.invoice_number,a.payment_number,
			b.product_summary,b.product_price,b.product_thumbnail
		from trade_tbl a join product_tbl b on a.product_no=b.product_no where a.product_no=#{productNo} and a.trade_buyer=#{tradeBuyer}

select bid_no,product_no,member_no,bid_price,bid_date,trade_status,trade_book,max_price,product_thumbnail,product_summary from (select rownum as rnum, c.* from (select b.bid_no,b.product_no,b.member_no,b.bid_price,to_char(b.bid_date,'yyyy-mm-dd') as bid_date, (select count(*) from trade_tbl t where t.product_no=b.product_no and t.trade_buyer=#{memberNo} and trade_state!='예약중') as my_trade, (select count(*) from trade_tbl t where t.product_no=b.product_no and t.trade_buyer!=#{memberNo}) as trade_status, (select count(*) from trade_tbl t where t.product_no=b.product_no and t.trade_buyer=#{memberNo} and trade_state='예약중') as trade_book, (select max(bid_price) from bid_tbl m where m.product_no=b.product_no) as max_price, p.product_thumbnail, p.product_summary from bid_tbl b join product_tbl p on b.product_no=p.product_no where b.member_no=45 and to_char(bid_date,'yyyy-mm-dd') between #{startDate} and #{endDate} order by 1 desc)c where my_trade=0
and trade_status = 0 and trade_book=0
and trade_book = 1
and trade_status = 1
) where rnum between #{start} and #{end}
